sys_script_client.script:
function onLoad() {
    //Type appropriate comment here, and begin script below
    //alert("Thank you for loading the NeedIt form.   When you close this alert you will be able to interact with the form.");

    //Message will dissapear in few seconds
    g_form.addInfoMessage('The top five fields in this form are mandatory');

    //Message will stay on form
    g_form.addErrorMessage('The top five fields in this form are mandatory');
    g_form.addFormMessage('info message', 'info');
    g_form.addFormMessage('warning message', 'warning');
    g_form.addFormMessage('error message', 'error');

	//addDecoration icon in front of field
    g_form.addDecoration('short_description', 'icon-star', 'Mark as Favorite', 'color-green');

    nowapi.log('!!!!!');
    nowapi.log(nowapi.g_document.getElement('#x_58872_needit_needit\\.short_description'));

    try {

        var control = g_form.getControl('short_description');
        Event.observe(control, 'mouseover', function() {
            nowapi.log('Your mouse is over the Description field.');

        });
    } catch (err) {}
}
