sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (newValue === '') {
        return;
    }
    var fieldName = 'u_what_needed';
    var whatneeded = g_form.getValue(fieldName);
    g_form.clearOptions(fieldName);

    var valuesMapping = {
        hr: [{
                value: 'hr1',
                label: 'Human Resources 1'
            },
            {
                value: 'hr2',
                label: 'Human Resources 2'
            }
        ],
        facilities: [{
                value: 'facilities1',
                label: 'Facilities 1'
            },
            {
                value: 'facilities2',
                label: 'Facilities 2'
            }
        ],
        legal: [{
                value: 'legal1',
                label: 'Legal 1'
            },
            {
                value: 'legal2',
                label: 'Legal 2'
            }
        ],
        other: {
            value: 'other',
            label: 'Other'
        }
    };

    valuesMapping[newValue].forEach(function(option) {
        g_form.addOption(fieldName, option.value, option.label);
    });
    g_form.addOption(fieldName, valuesMapping.other.value, valuesMapping.other.label);

    if (isLoading && !g_form.isNewRecord()) {
        g_form.setValue(fieldName, whatneeded);
    }
}
